import { useNavigate } from "react-router-dom";

// components
import MyPage from "../components/MyPage";

// styling 
import "../styles/src/LogInSection.scss";

// Context API 
import { UserLoggedInContextStore } from "../App";


function LogInSection () {
  // Context API 
  const userLoggedIn = useContext(UserLoggedInContextStore); 
  console.log(userLoggedIn);  // false 


  // let loggedIn = useRef(false);
  // console.log(loggedIn.current);  // false

    // 로그인 로직
    const [userId, setUserId] = useState("");
    const [userPwLogin, setUserPwLogin] = useState("");

    const url = 'http://localhost:9090/login';

    const submitToLogIn = async (e) => {
      e.preventDefault();

      try {
        const response = await fetch(url, {
          method: 'POST', 
          headers: {
            'Content-Type': 'application/json; charset=utf-8'
          },
          body: JSON.stringify({
            userId, 
            userPwLogin
          })
        });

        const data = await response.json(); 
        // console.log(data);  // data: {id: 83, name: '생쥐1', age: 0, gender: 'woman', email: 'sj1@gmail.com', …}
        // console.log(typeof data === 'object');   // true

        if (typeof data === 'object') {
          alert("정상적으로 로그인 되었습니다.");
          // loggedIn.current = !loggedIn.current;
          // console.log(loggedIn.current);

          // 마이페이지 보여주기 !!!

          userLoggedIn = !userLoggedIn;
          console.log(userLoggedIn);
        }


      } catch (error) {
        console.log("POST request XXXXXX!! - LoginSignup.js ");
      } 
    };  // reference:  https://www.youtube.com/watch?v=OUP-urBy1k4




    // useNavigate 
    let navigate = useNavigate();

    const moveToMyPage = () => {
      navigate("/my-page");
    };// reference: https://velog.io/@soryeongk/ReactRouterDomV6


    return (
      <UserLoggedInContextStore.Consumer>
        <div>안녕</div>
      </UserLoggedInContextStore.Consumer>
    )
}

export default LogInSection;
