// import React, { useEffect, useRef } from 'react';
import { useState, useEffect, useContext } from "react";

// 공통 components
// import Header from "./Header";
// import Footer from "./Footer";

// styling 
import "../styles/src/Mypage.scss"

// Context API 
import { UserLoggedInContextStore } from "../App";


let loggedInId;

if ((localStorage.getItem("authenticatedId") !== "loginFail")) {
  loggedInId = localStorage.getItem("authenticatedId");
}

// console.log(loggedInId);   // -> 유저의 이메일이 콘솔창에 찍힐 것!



function MyPage() {
  // Context API 
  let userLoggedIn = useContext(UserLoggedInContextStore); 

  // const [apiData, setApiData] = useState([]);
  // const [dataArr, setDataArr] = useState([]);


  // API 
  const url = 'http://localhost:9090/mypage';

  useEffect(() => {
    const asyncLoggedInIdPost = async () => {
      try {
        const response = await fetch(url, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json; charset=utf-8'
          },
          body: JSON.stringify({
            sessionId: loggedInId
          })
        });
        console.log(loggedInId);
        const data = await response.json();
        console.log("data ===> ", data);
        // setApiData(data.data);
        // console.log(apiData);   // 
      console.log("POST request to server done!! No problem! - 마이페이지!!");

    } catch(error) {
      console.log("POST request XXXXXX!! - 마이페이지!!");
    }
  } 
  asyncLoggedInIdPost();
// }, [sessionId]);
}, []);
// reference:  https://stackoverflow.com/questions/50046841/proper-way-to-make-api-fetch-post-with-async-await


  const MyPage = () => {
    return (
      <div id='Mypage_wrap'>
        <h1>마이페이지</h1>
        <form>
          <div className='first_box'>
            <ul> 
              <li className='name'><img src={require('../assets/img/laptop/user_name.png')} alt="이름이미지"></img></li>
              <span></span>
            </ul>
            <ul>
              <li>
                <img src={require('../assets/img/laptop/phone.png')} alt="전화번호 이미지"></img></li>
              <span></span>
            </ul>
          </div>
          <div className='second_box'>
            <ul>
              <li>
                <img src={require('../assets/img/laptop/user_email.png')} alt="이메일 이미지"></img>
              </li>
              <span></span>
            </ul>
            <ul>
              <li className="gender_box">
                  <img src={require('../assets/img/laptop/gender.png')} alt="남자 이미지"></img>
              </li>
              <span></span>
            </ul>
          </div>
        </form> 
      </div>
    )
  };

  console.log(userLoggedIn);
  return (
    <>
      {/* <Header /> */}
      <UserLoggedInContextStore.Consumer>
        {(userLoggedIn) => MyPage()}
      </UserLoggedInContextStore.Consumer>
      {/* <Footer /> */}
    </>
  )
}

export default MyPage;
