import React, {useState} from 'react';
import "../styles/src/Main.scss";
import BtnSlide from './BtnSlide';

export default function InnisfreeSlideBox () {

  const [slideindex, setSlideindex] = useState(1);
  // const numberRef = useRef(1);


  // useEffect(() => {

  //    setInterval(() => {
  //      if( slideindex !== 5 ){
  //       setSlideindex ( slideindex => slideindex +1);
  //      }else if( slideindex === 5 ){
  //       setSlideindex(1);
  //      }


  //    }, 3000);

  // }, []);



  const nextSlide = () => {
    if(slideindex !== 5){
        setSlideindex(slideindex + 1) 

    }else if(slideindex === 5){
        setSlideindex(1);

    }
  };

  const prevSlide = () => {
    if(slideindex !== 1){
      setSlideindex(slideindex - 1)
  }
  else if (slideindex === 1){
      setSlideindex(5)
  }
};

const movepage = index => {
  setSlideindex(index)
}

  return(
    <div className="slider">
    <div className='Innisfree_slide' >
      {Array.from({length: 5}).map((item,index)=>{
        return(
          <div key={item} className = { slideindex === index+1 ? "slide_anim" : "slide_none"}>
              <img src={process.env.PUBLIC_URL + `/img/Innisfree_slide${index+1}.jpg`} alt='Innisfree_slide_imgs'></img>
          </div>


        )
    })}


    </div>

    <BtnSlide moveSlide={nextSlide} derection={"next"}/>
    <BtnSlide moveSlide={prevSlide} derection={"prev"}/>

    <div className="dots">
      {Array.from({length: 5}).map((item, index) => (
        <div onClick={() => movepage(index + 1)}
          className={slideindex === index + 1 ? "filldot" : "emptydot"}></div>
      ))}
    </div>

  </div>

  );

};
