import React, { useLayoutEffect, useRef, useEffect, useState } from "react";
// import React, {useState} from 'react';
import "../styles/src/DrJartSlider.scss";


import SlideButton from './SlideButton';
// import BtnSlide from './BtnSlide';


function useInterval(callback, delay) {
  const savedCallback = useRef();
  useEffect(() => {
      savedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
      function tick() {
          savedCallback.current();
      }
      if (delay !== null) {
          let id = setInterval(tick, delay);
          return () => clearInterval(id);
      }
  }, [delay]);
}

function DrJartSlider() {
  const [currentIndex, setCurrentIndex] = useState(0)

  const slides = ['#2543CB', '#572240', '#226699', '#A01CA7', '#54BB0A'];

  // const handleSwipe = (direction) => {
  //   setCurrentIndex(currentIndex => currentIndex + direction)
  // };

  function handleSlide(index) {
    if (index < 0) {
      index = itemSize - 1;
    }
    else if (index > itemSize) {
      index = 0
    }
    setCurrentIndex(index);
  }

  function handleSwipe(direction) {
    handleSlide(currentIndex + direction)
  }
  // useInterval(() => {
  //   setCurrentIndex(currentIndex => currentIndex + 1);
  // }, 2000)

  return (
    <div className="slider_area">
      <div className="slider">
      <SlideButton direction="prev" onClick={() => handleSwipe(-1)} />
      <SlideButton direction="next" onClick={() => handleSwipe(1)} />
        <div className="slider_list">
          <div 
            className="slider_track"
            style={{transform: `translateX(${(-100 / slides.length) * (1 + currentIndex)}%)`}}
          >
            {
              slides.map((color, index) =>
                <div key={index} className="slider_item" >
                  <a>
                    <div style={{ background: color }}>
                      {index}
                    </div>
                  </a>
                </div>
              )
            }
          </div>
        </div>
      </div >
    </div >
  )
}


export default DrJartSlider;
